// models/Reimbursement.js
const { DataTypes } = require("sequelize");

module.exports = (sequelize) => {
  return sequelize.define("Reimbursement", {
    id: { type: DataTypes.INTEGER, autoIncrement: true, primaryKey: true },
    userId: { type: DataTypes.INTEGER, allowNull: false }, // FK to User
    username: { type: DataTypes.STRING, allowNull: false }, // denormalized for display
    formType: { type: DataTypes.STRING }, // which of the 5 forms
    amount: { type: DataTypes.FLOAT, allowNull: false },
    description: { type: DataTypes.TEXT },
    receipt: { type: DataTypes.STRING }, // filename/path
    ocrText: { type: DataTypes.TEXT },
    status: {
      type: DataTypes.ENUM("pending", "approved", "rejected"),
      defaultValue: "pending",
    },
  });
};
